VNT_MONTO_FACTURA DECIMAL(14,2),
VNT_IVA_FACTURA DECIMAL(14,2),
VNT_FECHA_FACTURA DATE,
CONSTRAINT PK_nIDF PRIMARY KEY(ID_REGISTRO)
);

